//! Storing Variables
const donateButton = $(".donate__button.freq"),
  footerSwitchButtons = $(".footer__top-switch-item"),
  footerContactsList = $(".footer__top-contacts"),
  footerLinksContentContainer = $(".footer__top-container"); //! Donation Page button
function donationPageSwitchButton(t) {
  let e = t.target;
  !0 !== e.classList.contains("active") && (donateButton.removeClass("active"), $(this).addClass("active"));
} //! Footer switch Button
function footerSwitchButtonHandler(t) {
  footerSwitchButtons.removeClass("active");
  let e = t.currentTarget;
  $(e).addClass("active");
  let i = $("<ul></ul>");
  i.addClass("footer__top-social mt-5"),
    i.html(`<li class="d-flex ai-c gap-3">
                      <i class="bi-instagram text-white fs-15 f-center"></i>
                      <a class="text-white customLink" href="#">KHC_charity</a>
                    </li>
                    <li class="d-flex gap-3 ai-c mt-3">
                      <i class="bi-twitter fs-15 f-center text-white"></i>
                      <a
                        class="text-white customLink"
                        href="mailto:info@yourgmail.com"
                        >Oslo.kindhearthcharity
                      </a>
                    </li>
                    <li class="d-flex gap-3 ai-c mt-3">
                      <i class="bi-whatsapp fs-15 f-center text-white"></i>
                      <a href="#" class="text-white customLink"
                        >KHC_charity.oslo</a
                      >
                    </li>
                    <li class="d-flex gap-3 ai-c mt-3">
                      <i class="bi-facebook fs-15 f-center text-white"></i>
                      <a href="#" class="text-white customLink">CharityTheme</a>
                    </li>
                    <li class="d-flex gap-3 ai-c mt-3">
                      <i class="bi-youtube fs-15 f-center text-white"></i>
                      <a href="#" class="text-white customLink"
                        >KindCharity__Norway</a
                      >
                    </li>`),
    "contacts" === $(e).attr("id")
      ? footerLinksContentContainer.html(footerContactsList)
      : footerLinksContentContainer.html(i);
} //! Calling functions
footerSwitchButtons.on("click", footerSwitchButtonHandler), donateButton.on("click", donationPageSwitchButton);
