//! Variables
const header = $(".header"),
  stickyHeader = $(".header__sticky"),
  headerNavLinks = $(".header__sticky-navLink"),
  footerContactsList = $(".footer__top-contacts"),
  footerSwitchButtons = $(".footer__top-switch-item"),
  footerLinksContentContainer = $(".footer__top-container"),
  headerHamburgerMenu = $(".header__sticky-hamburgerMenu"),
  headerHamburgerMenuInside = $(".header__sticky-hamburgerMenu *"),
  headerHamburgerMenuButton = $(".header__sticky-hamburgerIcon"),
  headerHamburgerMenuIcon = headerHamburgerMenuButton[0].children[0]; //! Functions
//! Sticky header
function stickyHeaderHandler() {
  let e = $(window).scrollTop();
  e > 0
    ? (stickyHeader.addClass("fixed"), header.css("height", "86px"))
    : (stickyHeader.removeClass("fixed"), header.css("height", "auto"));
} //! Smooth headerLinks scroll
function navLinksSmoothScrollHandler() {
  if ("" !== this.hash) {
    let e = this.hash,
      t = header.height(),
      a = $(e).offset().top - t + 1,
      r = $("html").scrollTop();
    3e3 > Math.abs(r - a)
      ? $("html , body").animate({ scrollTop: a }, 600)
      : $("html , body").animate({ scrollTop: a }, 1300);
  }
} //! Header navLinks class changing
function navLinksActiveClassHandler() {
  let e = [];
  for (i in (headerNavLinks.each(function () {
    e.push($(this).attr("href"));
  }),
  e))
    $(e[i]).position().top <= $(window).scrollTop() + 100 &&
      (headerNavLinks.removeClass("active"), headerNavLinks.eq(i).addClass("active"));
} //! Footer switch Button
function footerSwitchButtonHandler(e) {
  footerSwitchButtons.removeClass("active");
  let t = e.currentTarget;
  $(t).addClass("active");
  let a = $("<ul></ul>");
  a.addClass("footer__top-social mt-5"),
    a.html(`<li class="d-flex ai-c gap-3">
                    <i class="bi-instagram text-white fs-15 f-center"></i>
                    <a class="text-white customLink" href="#">KHC_charity</a>
                  </li>
                  <li class="d-flex gap-3 ai-c mt-3">
                    <i class="bi-twitter fs-15 f-center text-white"></i>
                    <a
                      class="text-white customLink"
                      href="mailto:info@yourgmail.com"
                      >Oslo.kindhearthcharity
                    </a>
                  </li>
                  <li class="d-flex gap-3 ai-c mt-3">
                    <i class="bi-whatsapp fs-15 f-center text-white"></i>
                    <a href="#" class="text-white customLink"
                      >KHC_charity.oslo</a
                    >
                  </li>
                  <li class="d-flex gap-3 ai-c mt-3">
                    <i class="bi-facebook fs-15 f-center text-white"></i>
                    <a href="#" class="text-white customLink">CharityTheme</a>
                  </li>
                  <li class="d-flex gap-3 ai-c mt-3">
                    <i class="bi-youtube fs-15 f-center text-white"></i>
                    <a href="#" class="text-white customLink"
                      >KindCharity__Norway</a
                    >
                  </li>`),
    "contacts" === $(t).attr("id")
      ? footerLinksContentContainer.html(footerContactsList)
      : footerLinksContentContainer.html(a);
} //! Header hamburger menu (Device with under 768px width)
function headerHamburgerMenuHandler() {
  headerHamburgerMenuIcon.classList.toggle("bi-x-lg"),
    !0 === headerHamburgerMenuIcon.classList.contains("bi-x-lg")
      ? (headerHamburgerMenu.css("display", "flex"),
        headerHamburgerMenuInside.css("visibility", "visible"),
        headerHamburgerMenuInside.animate({ opacity: "1" }, 250),
        stickyHeader.animate({ "max-height": 400 }, 800))
      : (setTimeout(function () {
          headerHamburgerMenu.css("display", "none");
        }, 750),
        headerHamburgerMenuInside.css("visibility", "hidden"),
        headerHamburgerMenuInside.animate({ opacity: "0" }, 250),
        stickyHeader.animate({ "max-height": "90px" }, 800));
} //! Calling Functions
$(window).on("scroll", stickyHeaderHandler),
  $(window).on("scroll", navLinksActiveClassHandler),
  headerNavLinks.on("click", navLinksSmoothScrollHandler),
  footerSwitchButtons.on("click", footerSwitchButtonHandler),
  headerHamburgerMenuButton.on("click", headerHamburgerMenuHandler); //! All swiper.js codes
//! Slider
var swiper = new Swiper(".mySwiper", {
    loop: !0,
    effect: "fade",
    simulateTouch: !1,
    autoplay: { delay: 5e3, disableOnInteraction: !1 },
    navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" },
  }),
  swiper = new Swiper(".mySwiper2", {
    spaceBetween: 0,
    centeredSlides: !0,
    effect: "fade",
    autoplay: { delay: 6e4, disableOnInteraction: !1 },
    pagination: { el: ".swiper-pagination", clickable: !0 },
  });
